mapper.select:
SELECT ctn_seq as ctnSeq, tpl_cd as tplCd, src_cd as srcCd, ctn_nm as ctnNm, dsp_st_dt as dspStDt, dsp_end_dt as dspEndDt, ctn_div as ctnDiv, dsp_yn as dspYn FROM internsc.tb_ctn WHERE 1=1 AND dsp_st_dt >= to_timestamp(#{dspStDt}||' 00:00:00.000','YYYY-MM-DD HH24:MI:SS') AND dsp_end_dt <= to_timestamp(#{dspEndDt}||' 23:59:59.999','YYYY-MM-DD HH24:MI:SS')
AND ctn_nm LIKE '%' || #{ctnNm} || '%'
AND dsp_yn = #{dspYn}
AND src_cd = #{srcCd}
AND tpl_cd = #{tplCd}
AND ctn_div = #{ctnDiv}

SELECT ctn_nm as ctnNm, ctn_seq as ctnSeq, ctn_div as ctnDiv, tplnm as tplNm, dsp_yn as dspYn, dsp_st_dt as dspStDt, dsp_end_dt as dspEndDt, cd_nm as srcNm FROM ( SELECT ctn_seq, tpl_cd, src_cd, ctn_nm, cd_nm as tplNm, dsp_st_dt, dsp_end_dt, ctn_div, dsp_yn FROM internsc.tb_ctn ctn INNER JOIN internsc.tb_cd cd on ctn.tpl_cd=cd.cd ) a inner join internsc.tb_cd cd on a.src_cd=cd.cd WHERE 1=1 AND dsp_st_dt >= to_timestamp(#{dspStDt}||' 00:00:00.000','YYYY-MM-DD HH24:MI:SS') AND dsp_end_dt <= to_timestamp(#{dspEndDt}||' 23:59:59.999','YYYY-MM-DD HH24:MI:SS')
AND ctn_nm LIKE '%' || #{ctnNm} || '%'
AND dsp_yn = #{dspYn}
AND src_cd = #{srcCd}
AND tpl_cd = #{tplCd}
AND ctn_div = #{ctnDiv}
ORDER BY #{orderBy}
LIMIT #{listSize} OFFSET #{startList}

SELECT
    COUNT(*) as listCnt
FROM internsc.tb_ctn
WHERE 1=1
AND dsp_st_dt >= to_timestamp(#{dspStDt}||' 00:00:00.000','YYYY-MM-DD HH24:MI:SS')
AND dsp_end_dt <= to_timestamp(#{dspEndDt}||' 23:59:59.999','YYYY-MM-DD HH24:MI:SS')

    AND ctn_nm LIKE '%' || #{ctnNm} || '%'


    AND dsp_yn = #{dspYn}


    AND src_cd = #{srcCd}


    AND tpl_cd = #{tplCd}


    AND ctn_div = #{ctnDiv}
