mapper.select:
SELECT cd_nm
FROM internsc.tb_cd
WHERE grp_cd = 'SRC_TY' AND cd = #{srcCd}

mapper.update:
UPDATE internsc.tb_ctn
SET src_cd = #{srcCd}
,   ctn_nm = #{ctnNm}
,   ctn_div = #{ctnDiv}
,   dsp_yn = #{dspYn}
,   cmt_yn = #{cmtYn}
,   cst_yn = #{cstYn}
,   pop_msg = #{popMsg}
,   mod_dt = #{modDt}
,   use_yn = #{useYn}
WHERE ctn_seq = #{ctnSeq}

UPDATE internsc.tb_ctn_det
SET url_addr = #{urlAddr}
,   mod_dt = #{modDt}
WHERE ctn_det_seq = #{ctnDetSeq}

UPDATE internsc.tb_img
SET path = #{path}
  ,   fe_nm = #{feNm}
  ,   enc_fe_nm = #{encFeNm}
  ,   fe_ext = #{feExt}
  ,   fe_sz = #{feSz}
  ,   mod_dt = #{modDt}
  ,   use_yn = #{useYn}
  ,   img_odr = #{imgOdr}
WHERE img_seq = #{imgSeq} AND img_grp_id = #{imgGrpId} AND img_ty_cd = #{imgTyCd}
