mapper.select:
SELECT cd_nm
FROM internsc.tb_cd
WHERE grp_cd = 'SRC_TY' AND cd = #{srcCd}

mapper.update:
UPDATE internsc.tb_ctn
SET src_cd = #{srcCd}
,   ctn_nm = #{ctnNm}
,   ctn_div = #{ctnDiv}
,   dsp_yn = #{dspYn}
,   cmt_yn = #{cmtYn}
,   cst_yn = #{cstYn}
,   pop_msg = #{popMsg}
,   mod_dt = #{modDt}
WHERE ctn_seq = #{ctnSeq}
